const PDFScenes = ["Fish Camp", "The Scar", "Ambush Site", "Maunder Bridge And Castle", "Star-handâ€™s Barrow", "Flume Fires", "In The Stinkweed", "Inkleholtz Lumber Camp"];
const NarchyScenes = ["Fishery", "Scar", "Forest Road", "Bridge", "Barrow", "Flume", "Stinkweed", "Lumber Camp"];
const offsets = [
  [0, 0],
  [-100, 200],
  [1000, 200],
  [900, 200],
  [250, -50],
  [0, 0],
  [700, 600],
  [100, 100]
]

if (game.scenes.current == undefined) {
  ui.notifications.info('This macro requires an active scene, please set an active scene first')
} else {
  ui.notifications.info("Starting PDF to Narchy")

  for (let i = 0; i < PDFScenes.length; i++) {
    for (const objCollectionName of ['notes', 'tokens', 'drawings']) {
      const objCollection = game.scenes.getName(PDFScenes[i])[objCollectionName]
      const datas = objCollection.map(o => o.toObject())
      const afterOffset = datas.map(o => ({ ...o,
        x: o.x * (offsets[i][2] || 1) + offsets[i][0],
        y: o.y * (offsets[i][2] || 1) + offsets[i][1]
      }))
      const embeddedName = canvas.scene[objCollectionName].documentClass.documentName
      await game.scenes.getName(NarchyScenes[i]).createEmbeddedDocuments(embeddedName, afterOffset);
    }
  }
  ui.notifications.info("Done")
}
